import React, { Component, Fragment } from "react";
import axios from "axios";
import "../css/game-list.css";
import Game from "./Game";
import Filters from "./Filters";
import Voved from "./Voved";
import dat from "../img.json";
class GameList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      gameData: [],
      activeButton: false
    };
  }
  componentDidMount() {
    axios("https://brainsterboxapi.herokuapp.com/games").then(data => {
      this.setState({
        gameData: data.data
      });
    });
  }
  activeBtn = () => {
    this.setState({ activeButton: !this.state.activeButton });
  };

  render() {
    return (
      <Fragment>
        <Voved />
        <Filters activeB={this.activeBtn} />
        <div className="div-padd">
          <div className="kos-div">
            <div className="kontra-kos-div">
              <div className="game-list">
                {this.state.gameData.map(el => (
                  <Game
                    id={el.id}
                    key={el.id}
                    img={dat.url}
                    slikaa={el.image}
                    // slikaa={dat.map(el => (
                    //   <img src={el.url} alt={el.id}></img>
                    //   ))}
                    slika={el.image}
                    title={el.title}
                    kategorija={el.category}
                    vremetraenje={el.timeFrame}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
        <Voved />
      </Fragment>
    );
  }
}

export default GameList;
