import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../css/header.css";
import "../css/sidebar.css";
import Logo from "../logo.png";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showSidebar: false
    };
  }
  toTop = () => {
    window.scrollTo(0, 0);
  };
  render() {
    return (
      <fragment>
        <div className="header">
          <div
            className="menu"
            onClick={() => this.setState({ showSidebar: true })}
          >
            <p>
              <i class="fa fa-bars" aria-hidden="true"></i>Мени
            </p>
          </div>
          <div className="brainsterLogo">
            <img src={Logo} onClick={this.toTop} alt="logo" />
          </div>
          <div className="linkovi">
            <a href="https://brainster.co/" target="_blank">
              <button className="obukiKopce">Обуки за компании</button>
            </a>
            <a href="https://brainster.co/" target="_blank">
              <button className="vrabotiKopce">Вработи наши студенти</button>
            </a>
          </div>
        </div>
        <div className={this.state.showSidebar ? "sidebar" : "hide-sidebar"}>
          <div className="menu-open">
            <div className="sidebar-top">
              <div className="sidebar-logo">
                <Link to="/">
                  <img src={Logo} />
                </Link>
              </div>
              <div className="zatvori-menu">
                <p onClick={() => this.setState({ showSidebar: false })}>
                  <span>
                    <i class="fa fa-times" aria-hidden="true"></i>
                  </span>
                  Затвори
                </p>
              </div>
            </div>
            <div className="sidebar-list">
              <ul>
                <li className="li-1">Регистрирај се</li>
                <li className="li-1">Најави се</li>
                <li>Регистрирај се</li>
                <Link to="/aboutUs">
                  <li>За нас</li>
                </Link>
                <a href="https://www.facebook.com/pg/brainster.co/photos/">
                  <li>Галерија</li>
                </a>
                <Link to="/contact">
                  <li>Контакт</li>
                </Link>
              </ul>
            </div>
          </div>
        </div>
      </fragment>
    );
  }
}

export default Header;
