import React, { Component } from 'react';
import axios from "axios";
import "../css/game-list.css";
import Game from "./Game";
class GameList extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            gameData : {}
         }
    }
    componentDidMount() {
        axios(
           'https://brainsterboxapi.herokuapp.com/games'
        ).then(data => {
            this.setState({
                gameData: data.data.find(
                    el => el.id === this.props.match.params.id
                  )
            })
        })
    }
    render() { 
        return ( 
            <div className="game-list">
                {this.state.gameData.map(el => (
                    <Game 
                    id={el.id}
                    key={el.id}
                    slika={el.image}
                    title={el.title}
                    kategorija={el.category}
                    vremetraenje={el.timeFrame}
                    />
                ))}
            </div>
         );
    }
}
 
export default GameList;
