import React, { Component } from 'react';

class GameDetails extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            gameDetail : []
         }
    }
    componentDidMount() {
        axios(
           'https://brainsterboxapi.herokuapp.com/games'
        ).then(data => {
            this.setState({
                gameDetail: data.data.find(el => el.id == this.props.match.params.id)
            })
        })
    }
    render() { 
        return ( 
        <h1>{el.title}</h1>
         )
  }
}


 
export default GameDetails;
