import React, { Component } from "react";
import "../css/filters.css";

class Filters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isActive1 : false,
      isActive2 : false,
      isActive3 : false,
      isActive4 : false,
      isActive5 : false,
      isActive6 : false,
      isActive7 : false,
      isActive8 : false,
      isActive9 : false,
      isActive10 : false,
      isActive11 : false,
      isActive12 : false,
      onThisFilter: false,
      onThisFilter2: false,
      onThisFilter3: false
    };
  }

  here1 = () => {
    {
      this.setState({ isActive1 : !this.state.isActive1 });
    }
  };
  showFilters = () => {
    {
      this.setState({ onThisFilter : !this.state.onThisFilter });
    }
  };
  showFilters2 = () => {
    {
      this.setState({ onThisFilter2 : !this.state.onThisFilter2 });
    }
  };
  showFilters3 = () => {
    {
      this.setState({ onThisFilter3 : !this.state.onThisFilter3 });
    }
  };

  render() {
    return (
      <div className="filters">
        <div className="all">
          <p>Show ALL</p>
          <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>ALL</button>
        </div>
        <div className="by-category">
          <p>Browse by category</p>
          <div
            className={
              this.state.onThisFilter ? "show-buttons" : "hide-buttons"
            }
          >
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>ENERGIZERS</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>INOVATION</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>ACION</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>SEALF-LEADERSHIP</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>TEAM</button>
          </div>

          <button id="button-category" onClick={this.showFilters}>
            by CATEGORY
          </button>
        </div>
        <div className="time">
          <p>Time frame (minutes)</p>
          <div
            className={
              this.state.onThisFilter2 ? "show-buttons2" : "hide-buttons2"
            }
          >
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>5 - 30</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>30 - 60</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>60 - 120</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>120 -240</button>
          </div>

          <button id="by-time" onClick={this.showFilters2}>
            by TIME
          </button>
        </div>
        <div className="group">
          <p>Group size</p>
          <div
            className={
              this.state.onThisFilter3 ? "show-buttons3" : "hide-buttons3"
            }
          >
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>2 - 10</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>10 - 40</button>
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>40 +</button>
          </div>

          <button id="group-btn" onClick={this.showFilters3}>
            by GROUP
          </button>
        </div>
      </div>
    );
  }
}

export default Filters;
