import React, { Component } from "react";
import "../css/filters.css";

class Filters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isActive1 : false,
      isActive2 : false,
      isActive3 : false,
      isActive4 : false,
      isActive5 : false,
      isActive6 : false,
      isActive7 : false,
      isActive8 : false,
      isActive9 : false,
      isActive10 : false,
      isActive11 : false,
      isActive12 : false,
      onThisFilter: false,
      onThisFilter2: false,
      onThisFilter3: false
    };
  }

  here1 = () => {
    {
      this.setState({ isActive1 : !this.state.isActive1 });
    }
  };
  here2 = () => {
    {
      this.setState({ isActive2 : !this.state.isActive2 });
    }
  };
  here3 = () => {
    {
      this.setState({ isActive3 : !this.state.isActive3 });
    }
  };
  here4 = () => {
    {
      this.setState({ isActive4 : !this.state.isActive4 });
    }
  };
  here5 = () => {
    {
      this.setState({ isActive5 : !this.state.isActive5 });
    }
  };
  here6 = () => {
    {
      this.setState({ isActive6 : !this.state.isActive6 });
    }
  };
  here7 = () => {
    {
      this.setState({ isActive7 : !this.state.isActive7 });
    }
  };
  here8 = () => {
    {
      this.setState({ isActive8 : !this.state.isActive8 });
    }
  };
  here9 = () => {
    {
      this.setState({ isActive9 : !this.state.isActive9 });
    }
  };
  here10 = () => {
    {
      this.setState({ isActive10 : !this.state.isActive10 });
    }
  };
  here11 = () => {
    {
      this.setState({ isActive11 : !this.state.isActive11 });
    }
  };
  here12 = () => {
    {
      this.setState({ isActive12 : !this.state.isActive12 });
    }
  };
  here13 = () => {
    {
      this.setState({ isActive13 : !this.state.isActive13 });
    }
  };
  showFilters = () => {
    {
      this.setState({ onThisFilter : !this.state.onThisFilter });
    }
  };
  showFilters2 = () => {
    {
      this.setState({ onThisFilter2 : !this.state.onThisFilter2 });
    }
  };
  showFilters3 = () => {
    {
      this.setState({ onThisFilter3 : !this.state.onThisFilter3 });
    }
  };

  render() {
    return (
      <div className="filters">
        <div className="all">
          <p>Show ALL</p>
          <button className={this.state.isActive13 ? "aktiven" : "neaktiven" } onClick={this.here13}>ALL</button>
        </div>
        <div className="by-category">
          <p>Browse by category</p>
          <div
            className={
              this.state.onThisFilter ? "show-buttons" : "hide-buttons"
            }
          >
            <button className={this.state.isActive1 ? "aktiven" : "neaktiven" } onClick={this.here1}>ENERGIZERS</button>
            <button className={this.state.isActive2 ? "aktiven" : "neaktiven" } onClick={this.here2}>INOVATION</button>
            <button className={this.state.isActive3 ? "aktiven" : "neaktiven" } onClick={this.here3}>ACION</button>
            <button className={this.state.isActive4 ? "aktiven" : "neaktiven" } onClick={this.here4}>SEALF-LEADERSHIP</button>
            <button className={this.state.isActive5 ? "aktiven" : "neaktiven" } onClick={this.here5}>TEAM</button>
          </div>

          <button id="button-category" onClick={this.showFilters}>
            by CATEGORY
          </button>
        </div>
        <div className="time">
          <p>Time frame (minutes)</p>
          <div
            className={
              this.state.onThisFilter2 ? "show-buttons2" : "hide-buttons2"
            }
          >
            <button className={this.state.isActive6 ? "aktiven" : "neaktiven" } onClick={this.here6}>5 - 30</button>
            <button className={this.state.isActive7 ? "aktiven" : "neaktiven" } onClick={this.here7}>30 - 60</button>
            <button className={this.state.isActive8 ? "aktiven" : "neaktiven" } onClick={this.here8}>60 - 120</button>
            <button className={this.state.isActive9 ? "aktiven" : "neaktiven" } onClick={this.here9}>120 -240</button>
          </div>

          <button id="by-time" onClick={this.showFilters2}>
            by TIME
          </button>
        </div>
        <div className="group">
          <p>Group size</p>
          <div
            className={
              this.state.onThisFilter3 ? "show-buttons3" : "hide-buttons3"
            }
          >
            <button className={this.state.isActive10 ? "aktiven" : "neaktiven" } onClick={this.here10}>2 - 10</button>
            <button className={this.state.isActive11 ? "aktiven" : "neaktiven" } onClick={this.here11}>10 - 40</button>
            <button className={this.state.isActive12 ? "aktiven" : "neaktiven" } onClick={this.here12}>40 +</button>
          </div>

          <button id="group-btn" onClick={this.showFilters3}>
            by GROUP
          </button>
        </div>
      </div>
    );
  }
}

export default Filters;
